from selenium import webdriverfrom selenium.webdriver.support.ui import WebDriverWaitfrom selenium.webdriver.support import expected_conditions as ECfrom selenium.webdriver.common.by import Byfor x in range(1000):    chrome_options = webdriver.ChromeOptions()    chrome_options.add_argument('--headless')    browser_s = webdriver.Chrome(chrome_options=chrome_options)    wait = WebDriverWait(browser_s, 10)    browser_s.get(url='https://www.jianshu.com/p/530dfe917afb')    print(x)    wait.until(EC.presence_of_element_located((By.CSS_SELECTOR, 'body > div.note > div.post > div.meta-bottom > div.like > div > div.btn-like > a')))    browser_s.quit()